    private void hoverEntered(JPanel btnPanel)
    {
        btnPanel.setBackground(new Color(0, 0, 0));
    }
    
    private void hoverExited(JPanel btnPanel)
    {
        btnPanel.setBackground(new Color(123, 205, 120));
    }
    
    public void showPanel(JPanel p)
    {
        p.setSize(1030, 590);
        p.setLocation(0, 0);
        
        this.contentPanel.removeAll();
        this.contentPanel.add(p, BorderLayout.CENTER);
        this.contentPanel.revalidate();
        this.contentPanel.repaint();
    }
    
